@deprecate_kwarg(old_arg_name='colors', new_arg_name='color')
@deprecate_kwarg(old_arg_name='data', new_arg_name='frame', stacklevel=3)
def parallel_coordinates(frame, class_column, cols=None, ax=None, color=None, use_columns=False, xticks=None, colormap=None, axvlines=True, axvlines_kwds=None, sort_labels=False, **kwds):
    "\n    Parallel coordinates plotting.\n\n    Parameters\n    ----------\n    frame : DataFrame\n    class_column : str\n        Column name containing class names\n    cols : list, optional\n        A list of column names to use\n    ax : matplotlib.axis, optional\n        matplotlib axis object\n    color : list or tuple, optional\n        Colors to use for the different classes\n    use_columns : bool, optional\n        If true, columns will be used as xticks\n    xticks : list or tuple, optional\n        A list of values to use for xticks\n    colormap : str or matplotlib colormap, default None\n        Colormap to use for line colors.\n    axvlines : bool, optional\n        If true, vertical lines will be added at each xtick\n    axvlines_kwds : keywords, optional\n        Options to be passed to axvline method for vertical lines\n    sort_labels : bool, False\n        Sort class_column labels, useful when assigning colors\n\n        .. versionadded:: 0.20.0\n\n    kwds : keywords\n        Options to pass to matplotlib plotting method\n\n    Returns\n    -------\n    class:`matplotlib.axis.Axes`\n\n    Examples\n    --------\n    >>> from matplotlib import pyplot as plt\n    >>> df = pd.read_csv('https://raw.github.com/pandas-dev/pandas/master'\n                        '/pandas/tests/data/iris.csv')\n    >>> pd.plotting.parallel_coordinates(\n            df, 'Name',\n            color=('#556270', '#4ECDC4', '#C7F464'))\n    >>> plt.show()\n    "
    plot_backend = _get_plot_backend('matplotlib')
    return plot_backend.parallel_coordinates(frame=frame, class_column=class_column, cols=cols, ax=ax, color=color, use_columns=use_columns, xticks=xticks, colormap=colormap, axvlines=axvlines, axvlines_kwds=axvlines_kwds, sort_labels=sort_labels, **kwds)